INSERT INTO `d_learnpelajaran` (`id`, `mata_pelajaran`, `judul`, `gambar`, `dialog1`, `dialog2`, `dialog3`, `tatabahasa1`, `tatabahasa2`, `cdate`, `kataungkapan`, `is_quiz`, `is_active`) VALUES
(3, 'PELAJARAN 3', 'Shinagawa-eki made onegai-shimasu <br/>   <br/> Tolong ke stasiun Shinagawa', 'media/upload//2018/02/pelajaran3.jpg', '<p style="text-align:center"><img alt="" width="860" height="507" src="http://localhost/japunis.com//media/upload//2018/02/lesson3.jpg" /></p><div style="text-align:center" class="ckeditor-html5-audio"><audio controls="controls" src="http://localhost/japunis.com//media/upload//2018/02/lesson3_d1.mp3" controlslist="nodownload">&nbsp;</audio></div><hr /><p><span style="font-family:roboto,sans-serif; font-size:12pt"><em>Untenshu = Pengemudi Taksi</em></span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">Kim pergi ke Stasiun Shinagawa dengan taksi.</span></p><table style="width:100%"><tbody><tr><td style="height:21px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;K:</strong></span></td><td style="height:21px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Shinagawa-eki made onegai-shimasu.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;<strong>U:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Hai, Shinagawa-eki desu ne.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>K:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Ee. Sumimasen, koko kara Shinagawa-eki made nan-pun gurai desu ka.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;U:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Soo desu ne, juu-go-fun gurai desu.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>K:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Soo desu ka.</span></td></tr></tbody></table><hr /><table style="width:100%"><tbody><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">K:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Tolong ke stasiun Shinagawa</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">&nbsp;PT:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Ke stasiun Shinagawa?</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">&nbsp;K:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Betul. Permisi, berapa lama waktu yang dibutuhkan dari sini ke Stasiun Shinagawa?</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">PT:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Sebentar, sekitar 15 menit.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">&nbsp;K:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Baiklah.</span></td></tr></tbody></table><hr /><p><span style="font-family:roboto,sans-serif; font-size:14pt">Kosa Kata</span></p><table style="width:100%"><tbody><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">eki</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">stasiun</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">made</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">ke</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">onegai shimasu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">tolong</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">untenshu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">supir</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">~ne</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">~, benar ?</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">koko</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di sini (kata ganti tempat)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">~kara</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">dari ~</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">nan-pun</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">berapa lama ?</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">fun</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">menit</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">gurai</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">tentang</span></td></tr></tbody></table>', '<div style="text-align:center" class="ckeditor-html5-audio"><audio controls="controls" src="http://localhost/japunis.com//media/upload//2018/02/lesson3_d2.mp3" controlslist="nodownload">&nbsp;</audio></div><hr /><p><span style="font-family:roboto,sans-serif; font-size:12pt"><em>Untenshu = Pengemudi Taksi<br />Okyaku&nbsp; &nbsp; = Panggilan kepada pelanggan</em></span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">Tiba di stasiun Shinagawa, Kim bertanya tentang biaya perjalanan</span></p><table style="width:100%"><tbody><tr><td style="height:21px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;U:</strong></span></td><td style="height:21px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Okyaku-san, Shinagawa-eki desu.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;<strong>K:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Ikura desu ka.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>U:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Sen-go-hyaku-ni-juu-en desu.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;K:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Hai, dewa kore de onegai-shimasu.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>U:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Hai, yon-hyaku-hachi-juu-en no otsuri desu. Arigatoo gozaimashita.</span></td></tr></tbody></table><hr /><table style="width:100%"><tbody><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">PT:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Nyonya, kita telah tiba di stasiun Shinagawa.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">&nbsp;K:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Berapa ongkosnya?</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">&nbsp;PT:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">1,520 yen</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">K:</span></strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Baik, ini uangnya</span></td></tr><tr><td style="height:0px; text-align:right; width:15%"><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">&nbsp;PT:</span></strong></span></td><td style="height:0px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Ok, ini kembaliannya 480 yen. Terima kasih banyak.</span></td></tr></tbody></table><hr /><p><span style="font-family:roboto,sans-serif; font-size:14pt">Kosa Kata</span></p><table style="width:100%"><tbody><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">kyaku</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">pelanggan (penumpang)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">ikura</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">berapa?</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">-en</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">yen (mata uang Jepang)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">dewa</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">maka</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">otsuri</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">uang kembali (kembalia)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">arigatoo gozaimashita</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">terima kasih banyak </span></td></tr></tbody></table>', '<div style="text-align:center" class="ckeditor-html5-audio"><audio controls="controls" src="http://localhost/japunis.com//media/upload//2018/02/lesson3_d3.mp3" controlslist="nodownload">&nbsp;</audio></div><hr /><p><span style="font-family:roboto,sans-serif; font-size:12pt">Kim bertemu dengan Schmidt dan Sato menjelaskan tentang kegiatan di hari ini.</span></p><table style="width:100%"><tbody><tr><td style="height:21px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;Sa:</strong></span></td><td style="height:21px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Korekara Shinkansen de Kobe e ikimasu. Juu-ji ni tsukimasu.&nbsp;</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Soshite gogo Numazu e ikimasu. Yoru Tokyo e kaerimasu.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>Sc + K:</strong></span></td><td style="height:17px; width:85%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Wakarimashita. Yoroshiku onegai-shimasu.</span></td></tr></tbody></table><hr /><table style="width:100%"><tbody><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>Sa:</strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Sekarang kita akan pergi ke Kobe dengan Shinkanse. Kita akan tiba di sana jam 10.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;</strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Lalu pada sore hari pergi ke Numazu. Lalu kita akan kembali ke Tokyo malam hari.</span></td></tr><tr><td style="height:17px; text-align:right; width:15%"><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>&nbsp;Sc + K:</strong></span></td><td style="height:17px; width:85%"><span style="font-family:roboto,sans-serif; font-size:12pt">Baik. Senang Anda memandu kami.</span></td></tr></tbody></table><hr /><p><span style="font-family:roboto,sans-serif; font-size:14pt">Kosa Kata</span></p><table style="width:100%"><tbody><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">korekara</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">mulai sekarang</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">shinkansen</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">kereta kecepatan tinggi</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">Kobe</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">kota Kobe</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">ikimasu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">pergi</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">-ji</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">jam</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">tsukimasu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">tiba</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">soshite</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">maka</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">gogo</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">sore hari</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">Numazu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">kota Numazu</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">yoru</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">malam</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">kaerimasu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">kembali</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">wakarimashita</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">oke</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">mainichi</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">setiap hari</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">hatarikimasu</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">bekerja</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">ashita</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">besok</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">hikooki</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">pesawat terbang</span></td></tr></tbody></table>', '<p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>1.Kata Ganti Tunjuk &amp; Kata Sifat</strong></span></p><p><span style="font-family:roboto,sans-serif"><em>*KB = Kata Benda</em></span></p><table style="width:100%"><tbody><tr><td style="text-align:center; width:45.4545%"><p><span style="font-size:12pt"><strong><span style="font-family:roboto,sans-serif">Kata Tunjuk Benda</span></strong></span></p></td><td style="text-align:center; width:29.5187%"><p><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">kore</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">sore<br />are</span></p></td><td style="text-align:center; width:22.8877%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;<span style="color:#ff6600">dore</span></span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">yang mana?</span></td></tr><tr><td style="text-align:center; width:45.4545%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;<strong>Kata Tunjuk Sifat</strong></span></td><td style="text-align:center; width:29.5187%"><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#ff6600">kono</span>+KB</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#ff6600">sono</span>+KB</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#ff6600">ano</span>+KB</span></td><td style="text-align:center; width:22.8877%"><p><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#ff6600">dono</span>KB</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">yang manaKB&nbsp;</span></p></td></tr><tr><td style="text-align:center; width:45.4545%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;<strong>Kata Ganti Tempat</strong></span></td><td style="text-align:center; width:29.5187%"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">&nbsp;koko</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">soko</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">asoko</span></td><td style="text-align:center; width:22.8877%"><p><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">doko</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">dimana&nbsp;</span></p></td></tr><tr><td style="text-align:center; width:45.4545%"><p><span style="font-family:roboto,sans-serif; font-size:12pt"><strong>Kata Ganti Arah, Sisi, Tempat</strong></span></p></td><td style="text-align:center; width:29.5187%"><p><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">kochira</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">sochira</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">achira</span></p></td><td style="text-align:center; width:22.8877%"><p><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">dochira</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">dimana/</span><span style="font-family:roboto,sans-serif; font-size:12pt">arah mana?</span></p></td></tr></tbody></table><table style="width:100%"><tbody><tr><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt"><img alt="" width="136" height="136" src="http://localhost/japunis.com//media/upload//2018/02/koko.png" /></span><br />&nbsp;</td><td style="text-align:center"><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">kore</span></td></tr><tr><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt"><img alt="" width="158" height="123" src="http://localhost/japunis.com//media/upload//2018/02/soko.png" /></span></td><td style="text-align:center"><span style="color:#ff6600; font-family:roboto,sans-serif"><span style="font-size:16px">soko</span></span></td></tr><tr><td style="text-align:center"><img alt="" width="178" height="153" src="http://localhost/japunis.com//media/upload//2018/02/asoko.png" /></td><td style="text-align:center"><span style="color:#ff6600; font-family:roboto,sans-serif"><span style="font-size:16px">are</span></span></td></tr></tbody></table><p>&nbsp;</p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>2.KB1&nbsp; kara&nbsp; KB2&nbsp; made (dari....sampai....)</strong></span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>a)Tempat....Tempat....</strong></span></p><p><span style="font-family:roboto,sans-serif"><span style="color:#ff6600">Kara</span>.......<span style="color:#ff6600">made</span>, artinya dari satu tempat ke tempat lainnya.&nbsp;</span></p><p><span style="color:#333399; font-family:roboto,sans-serif">Koko <span style="color:#ff6600">kara</span> Shinagawa-eki <span style="color:#ff6600">made</span> nan-pun gurai desu ka.</span><br /><span style="font-family:roboto,sans-serif">Berapa lama waktu yang dibutuhkan dari sini ke Stasiun Shinagawa?</span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>b)Waktu.....Jam</strong></span></p><p><span style="font-family:roboto,sans-serif"><span style="color:#ff6600">Kara</span>.......<span style="color:#ff6600">made</span>, artinya dari satu waktu ke waktu lainnya.</span></p><p><span style="color:#333399; font-family:roboto,sans-serif">ku-ji <span style="color:#ff6600">kara</span> juu-ichi-ji <span style="color:#ff6600">made</span></span><br /><span style="font-family:roboto,sans-serif">dari jam 9 ke jam 11</span></p><p><span style="color:#333399; font-family:roboto,sans-serif">asa <span style="color:#ff6600">kara</span> ban <span style="color:#ff6600">made</span></span><br /><span style="font-family:roboto,sans-serif">dari pagi sampai malam</span></p><p>&nbsp;</p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>3.Waktu </strong></span></p><p><span style="font-family:roboto,sans-serif">Untuk menyatakan waktu, gunakan satuan bilangan <span style="color:#ff6600">ji</span> (jam) dan <span style="color:#ff6600">fun/pun</span> (menit).</span></p><p><span style="font-family:roboto,sans-serif">Untuk lebih lengkap ada di tab Kata &amp; Ungkapan di bawah.</span></p><hr /><p><span style="font-family:roboto,sans-serif; font-size:14pt">Kosa Kata</span></p><table style="width:100%"><tbody><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">dore</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">yang mana (kata tunjuk benda)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">dono</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">yang mana (kata tunjuk sifat)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">soko</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di situ (kata ganti tempat)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">asoko</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di sana (kata ganti tempat)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">doko</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di mana (kata ganti tempat)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">sochira</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di situ (kata ganti arah)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">achira</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di sana (kata ganti arah)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">dochira</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">di mana/yang mana (kata ganti arah)</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">asa</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">pagi</span></td></tr><tr><td><strong><span style="font-family:roboto,sans-serif; font-size:12pt">ban</span></strong></td><td><span style="font-family:roboto,sans-serif; font-size:12pt">malam</span></td></tr></tbody></table>', '<p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>4.Ikura </strong>(Berapa?)</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#ff6600">Ikura</span> adalah kata tanya yang digunakan untuk menanyakan harga atau kuantitas sesuatu.</span></p><p><span style="color:#333399; font-family:roboto,sans-serif; font-size:12pt">Kore wa <span style="color:#ff6600">ikura</span> desu ka.</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">Berapa ini?</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>5.Kata Kerja Bentuk -masu</strong></span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">Dalam semua kalimat, akhiran kata kerja diakhiri dengan <span style="color:#ff6600">masu</span> untuk kesopanan.</span></p><ul><li><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#333399">iki<span style="color:#ff6600">masu</span>&nbsp; &nbsp; &nbsp; &nbsp;</span>(Pergi)</span></li><li><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#333399">tsuki<span style="color:#ff6600">masu</span>&nbsp; &nbsp;</span>(Tiba)</span></li><li><span style="font-family:roboto,sans-serif; font-size:12pt"><span style="color:#333399">kaeri<span style="color:#ff6600">masu</span></span>&nbsp;&nbsp;&nbsp;(Kembali)</span></li></ul><p><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>6.Perubahan kata kerja bentuk &ndash;masu</strong></span></p><table style="width:100%"><tbody><tr><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></td><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt">Bukan lampau (sekarang dan / atau masa depan)</span></td><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt">Lampau</span></td></tr><tr><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt">Afirmatif</span></td><td style="text-align:center"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Iki </span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">masu</span></td><td style="text-align:center"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Iki</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">mashita</span></td></tr><tr><td style="text-align:center"><span style="font-family:roboto,sans-serif; font-size:12pt">Negatif</span></td><td style="text-align:center"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Iki</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">masen</span></td><td style="text-align:center"><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">Iki</span><br /><span style="color:#ff6600; font-family:roboto,sans-serif; font-size:12pt">masendeshita</span></td></tr></tbody></table><p><span style="font-family:roboto,sans-serif; font-size:12pt">Bentuk lampau digunakan untuk merujuk pada tindakan dan keadaan saat ini dan masa depan. Orang Jepang tidak membedakan antara keduanya secara gramatikal.</span></p><p><span style="color:#333399; font-family:roboto,sans-serif; font-size:12pt">Watashi wa <span style="color:#ff6600">mainichi</span> hatarakimasu.</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">Saya bekerja setiap hari.&nbsp;&nbsp;</span></p><p><span style="color:#333399; font-family:roboto,sans-serif; font-size:12pt">Watashi wa <span style="color:#ff6600">ashita</span> hatarakimasu.</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">Saya akan bekerja besok</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>7.Kata Benda (Tempat) + e&nbsp; </strong>Ke (datang / pergi / pulang) (tempat)</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">tempat....ke....tempat</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">Partikel <span style="color:#ff6600">e</span> menandai kata benda yang menunjukkan arah, dan digunakan dengan kata kerja yang memiliki makna arah seperti <span style="color:#ff6600">ikimasu</span>, <span style="color:#ff6600">kimasu</span>, <span style="color:#ff6600">kaerimasu</span>.</span></p><p><span style="color:#333399; font-family:roboto,sans-serif; font-size:12pt">Kobe e <span style="color:#ff6600">ikimasu</span>. </span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">Saya akan pergi ke Kobe.</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>8.Kata Benda (Kendaraan) + de&nbsp;&nbsp;</strong>Dengan (pesawat, kereta api, dan lainnya)</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">Partikel <span style="color:#ff6600">de</span> lalu diikuti dengan kata benda untuk menunjukkan sarana transportasi.</span></p><p><span style="color:#333399; font-family:roboto,sans-serif; font-size:12pt">Hikooki <span style="color:#ff6600">de</span> ikimasu. </span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">Saya akan pergi dengan pesawat.</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></p><p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>9.Kata Benda (Waktu) + ni&nbsp; </strong>Pada (waktu)</span></p><p><span style="font-family:roboto,sans-serif; font-size:12pt">Inti waktu ketika sebuah tindakan terjadi ditunjukkan dengan meletakkan partikel <span style="color:#ff6600">ni</span> setelah kata benda yang menunjukkan waktu, tapi hanya bila waktu dinyatakan menggunakan angka.</span></p><p><span style="color:#333399; font-family:roboto,sans-serif; font-size:12pt">Hachi-ji yon-jup-pun <span style="color:#ff6600">ni</span> tsukimasu.</span><br /><span style="font-family:roboto,sans-serif; font-size:12pt">Kita akan tiba pada jam 8:40.</span></p>', '0000-00-00 00:00:00', '<p><span style="font-family:roboto,sans-serif; font-size:14pt"><strong>Josuushi</strong><br />Satuan Bilangan</span></p><table style="width:100%"><tbody><tr><td style="width:10%"><span style="font-family:roboto,sans-serif; font-size:12pt">&nbsp;</span></td><td style="text-align:left; 